import React, { Component } from 'react';
import * as THREE from 'three';
import BIRDS from 'vanta/dist/vanta.birds.min';


class Birds extends Component {
  constructor() {
    super()
    this.vantaRef = React.createRef()
  }

  componentDidMount() {
    this.vantaEffect = BIRDS({
      el: this.vantaRef.current,
      
      mouseControls: true,
      touchControls: true,
      gyroControls: false,
      minHeight: 500.00,
      minWidth: 500.00,
      sunColor: 0x0,
      sunGlareColor: 0xff3f00,
      speed: 0.00,
      scale: 1.00,
  scaleMobile: 1.00,
      
      
      


      THREE: THREE
    })
  }
  componentWillUnmount() {
    if (this.vantaEffect) this.vantaEffect.destroy()
  }
  render() {
    return <div ref={this.vantaRef}>
      

      <table>
            <thead>
            <tr>
                <th>Title</th>
                <th>Company</th>
                <th>Actions</th>
            </tr>
            </thead>
            <tbody>
                {
                    props.titles.length > 0 ?
                    props.titles.map(title => (
                <tr key={title.id}>
                <td>{title.title}</td>
                <td>{title.company}</td>
                <td>
                <button className="button muted-button" onClick={
                    () => {props.editRow(title)}
                    }>Edit</button>
                <button className="button muted-button" onClick={() => {if (window.confirm('Are you sure you wish to delete this item?')) props.deleteTitle(title.id)}}>
                    
                    
                    
                    
                    Delete
                    </button>
                </td>
            </tr>

                 ))   : (
                    <tr>
                    <td colSpan={3}>No Titles</td>
                    </tr>  
                 )
                }
            
            </tbody>
            
        </table>



    </div>
  }
}

export default Birds;
